css:
.print-heading {
    border: 0;
}
#header-html {
    display: none;
}
#quotation-heading {
    position: absolute;
    top: 250px;
    right: 50px;
    font-size: 69px !important;
    font-weight: bold;
}
.print-format {
    padding: 10px !important;
}

* {
    padding: 0 !important;
    margin: 0 !important;
    box-sizing: border-box !important;
    font-size: 15px !important;
    line-height: 20px;
}

.print-format {
    margin: 0;
    background-image: url(/files/image_5.png);
    background-repeat: no-repeat;
    background-position: center center;
    background-size: 100%;
}


.letter-head {
    width: 100%;
    margin: 0;
}

.top-div{
    width: 100%;
    display: flex;
    flex-direction: row;
    justify-content: space-between;
}

.heading-table td {
    padding: 5px;
}

.item-table { 
    width: 100%;
    margin: auto;
}
.item-table, .item-table th, .item-table td{
    border: 1px solid black;
    border-collapse: collapse;
    font-size: 15px;
    padding: 5px !important;
}
.item-table th {
    color: black;
    font-weight: bold;
}
