script:
frappe.ui.form.on('Project', {
    onload: function(frm) {
        // Fetch custom_rate_selection and rate field values from Sales Order Item
        frappe.call({
            method: 'al_sulati.al_sulati.custom.project_rate.fetch_rate_values',
            args: {
                sales_order: frm.doc.sales_order // Assuming you have a field named 'sales_order' in Project to link to Sales Order
            },
            callback: function(r) {
                if(r.message) {
                    // Set the fetched values in custom_rate_type and custom_rate fields
                    frm.set_value('custom_rate_type', r.message.custom_rate_selection);
                    frm.set_value('custom_rate', r.message.rate);
                }
            }
        });
    }
});

frappe.ui.form.on('Sales Order', {
   refresh: function(frm) {
       setTimeout(() => {
        frm.remove_custom_button('Sales Invoice', 'Create');
        frm.remove_custom_button('Purchase Order', 'Create');
        frm.remove_custom_button('Subscription', 'Create');
        frm.remove_custom_button('Engagement Record', 'Create');
        frm.remove_custom_button('Work order', 'Create');
        frm.remove_custom_button('Material Request', 'Create');
        frm.remove_custom_button('Pick List', 'Create');
        frm.remove_custom_button('Payment', 'Create');
        frm.remove_custom_button('Work Order', 'Create');
        frm.remove_custom_button('Request for Raw Materials', 'Create');
        frm.remove_custom_button('Payment Request', 'Create');
       }, 10);
   }
});

frappe.ui.form.on('Project', {
onload (frm) {
setTimeout(() => {
    $("[data-doctype='Sales Invoice']").hide()
    $("[data-doctype='Task']").hide()
    $("[data-doctype='Project Update']").hide()
    $("[data-doctype='Issue']").hide()
    $("[data-doctype='Stock Entry']").hide()

    $(".col-md-4").each(function() {

        var sectionName = $(this).find(".form-link-title").text().trim();

            if (sectionName === "Material"|| sectionName === "Purchase" || sectionName === "Claims" ) {
                $(this).hide(); 
            }
       });
       }, 10);
    }
});

frappe.ui.form.on('Sales Order Item', {
    custom_rate_selection: function(frm, cdt, cdn) {
        var child = locals[cdt][cdn];
        if (!child.custom_rate_selection) {
            frappe.model.set_value(cdt, cdn, 'rate', 0);
            return;
        }
        else if (child.custom_rate_selection === 'Monthly') {
            frappe.call({
                method: 'al_sulati.al_sulati.custom.sales_order_amount.fetch_custom_rate',
                args: {
                    doctype: 'Quotation Item',
                    fieldname: 'custom_monthly_rate',
                    prevdoc_docname: child.prevdoc_docname
                },
                callback: function(response) {
                    if (response.message) {
                        frappe.model.set_value(cdt, cdn, 'rate', response.message);
                    }
                }
            });
        } else if (child.custom_rate_selection === 'Weekly') {
             frappe.call({
                method: 'al_sulati.al_sulati.custom.sales_order_amount.fetch_custom_rate',
                args: {
                    doctype: 'Quotation Item',
                    fieldname: 'custom_weekly_rate',
                    prevdoc_docname: child.prevdoc_docname
                },
                callback: function(response) {
                    if (response.message) {
                        frappe.model.set_value(cdt, cdn, 'rate', response.message);
                    }
                }
            });
        } else if (child.custom_rate_selection === 'Daily') {
             frappe.call({
                method: 'al_sulati.al_sulati.custom.sales_order_amount.fetch_custom_rate',
                args: {
                    doctype: 'Quotation Item',
                    fieldname: 'custom_daily_rate',
                    prevdoc_docname: child.prevdoc_docname
                },
                callback: function(response) {
                    if (response.message) {
                        frappe.model.set_value(cdt, cdn, 'rate', response.message);
                    }
                }
            });
        }
    }
});

frappe.ui.form.on('Quotation', {
    onload: function(frm) {
        // Fetch the signature from the active Employee
        frappe.call({
            method: 'al_sulati.al_sulati.custom.quotation_signature.fetch_active_employee_signature',
            callback: function(r) {
                if(r.message) {
                    // Set the fetched signature in the Quotation doctype
                    frm.set_value('custom_signature', r.message);
                }
            }
        });
    }
});

frappe.ui.form.on("Sales Order", {
    onload: function (frm) {

        frm.set_query("employee_name","custom_employee", function () {
            return {
                "filters": {
                    "designation":"Operator",
                    "custom_employee_availabilty":"idle",

                }
            };
        });
    },
});
frappe.ui.form.on('Sales Order', {
    after_save: function(frm) {
        if (frm.doc.docstatus === 1) {
           frappe.call({
                method: 'al_sulati.al_sulati.custom.EmployeeStatus.updateEmpStatus',
                args:{
                    doc: frm.doc
           }
            });

        }
    }
});
