script:
frappe.ui.form.on('Sales Order Item', {
    custom_rate_selection: function(frm, cdt, cdn) {
        var child = locals[cdt][cdn];
        if (!child.custom_rate_selection) {
            frappe.model.set_value(cdt, cdn, 'rate', 0);
            return;
        }
        else if (child.custom_rate_selection === 'Monthly') {
            frappe.call({
                method: 'al_sulati.al_sulati.custom.sales_order_amount.fetch_custom_rate',
                args: {
                    doctype: 'Quotation Item',
                    fieldname: 'custom_monthly_rate',
                    prevdoc_docname: child.prevdoc_docname
                },
                callback: function(response) {
                    if (response.message) {
                        frappe.model.set_value(cdt, cdn, 'rate', response.message);
                    }
                }
            });
        } else if (child.custom_rate_selection === 'Weekly') {
             frappe.call({
                method: 'al_sulati.al_sulati.custom.sales_order_amount.fetch_custom_rate',
                args: {
                    doctype: 'Quotation Item',
                    fieldname: 'custom_weekly_rate',
                    prevdoc_docname: child.prevdoc_docname
                },
                callback: function(response) {
                    if (response.message) {
                        frappe.model.set_value(cdt, cdn, 'rate', response.message);
                    }
                }
            });
        } else if (child.custom_rate_selection === 'Daily') {
             frappe.call({
                method: 'al_sulati.al_sulati.custom.sales_order_amount.fetch_custom_rate',
                args: {
                    doctype: 'Quotation Item',
                    fieldname: 'custom_daily_rate',
                    prevdoc_docname: child.prevdoc_docname
                },
                callback: function(response) {
                    if (response.message) {
                        frappe.model.set_value(cdt, cdn, 'rate', response.message);
                    }
                }
            });
        }
    }
});

frappe.ui.form.on('Quotation', {
    onload: function(frm) {
        // Fetch the signature from the active Employee
        frappe.call({
            method: 'al_sulati.al_sulati.custom.quotation_signature.fetch_active_employee_signature',
            callback: function(r) {
                if(r.message) {
                    // Set the fetched signature in the Quotation doctype
                    frm.set_value('custom_signature', r.message);
                }
            }
        });
    }
});

frappe.ui.form.on("Sales Order", {
    onload: function (frm) {

        frm.set_query("employee_name","custom_employee", function () {
            return {
                "filters": {
                    "designation":"Operator",
                    "custom_employee_availabilty":"idle",

                }
            };
        });
    },
});
frappe.ui.form.on('Sales Order', {
    after_save: function(frm) {
        if (frm.doc.docstatus === 1) {
           frappe.call({
                method: 'al_sulati.al_sulati.custom.EmployeeStatus.updateEmpStatus',
                args:{
                    doc: frm.doc
           }
            });

        }
    }
});
