css:
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}
.print-format {
    padding: 20px;
    margin: 0;
    border: 1px solid black;
    background-image: url(/files/image_5.png);
    background-repeat: no-repeat;
    background-position: center;
    background-size: 100%;
}

.letter-head {
    width: 100%;
}

.top-div{
    width: 100%;
    display: flex;
    flex-direction: row;
    justify-content: space-between;
}
.item-table { 
    width: 90%;
    margin: auto;
}
.item-table, .item-table th, .item-table td{
    border: 1px solid black;
    border-collapse: collapse;
    font-size: 12px;
}
.item-table th {
    color: black;
    font-weight: bold;
}
