css:
* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}
.print-format {
    padding: 20px;
    margin: 0;
    background-image: url("/files/watermark.png");
    background-repeat: no-repeat;
    background-position: center;
    background-size: 100%;

}

.letter-head {
    width: 100%;
}

.top-div{
    width: 100%;
    display: flex;
    flex-direction: row;
    justify-content: space-between;
}
.item-table { 
    width: 90%;
    margin: auto;
}
.item-table, .item-table th, .item-table td{
    border: 1px solid black;
    border-collapse: collapse;
    font-size: 12px;
}
.item-table th {
    color: black;
    font-weight: bold;
}
.corner{
    position:relative !important;
    left:0px !important;
}
.quotpos{
position:absolute;
top:20%;
right:7%;
font-weight:bold;
}
.quotpos h2{
 font-size:45px !important;
 font-weight:bold;
}
.attention{
    width:50%;
    font-size:14px;
    margin:0;
    padding:0;
}
.print-format .corner td{
    padding:0px 0px 10px 0px  !important;   
}
.item-table td{
    text-align:center !important;
}
.print-format p {
    margin:0px;

}
